/* Scroll suave */
html {
    scroll-behavior: smooth;
}

/* Efeitos visuais adicionais */
.benefit-card:nth-child(even) {
    animation-delay: 0.2s;
}

.exclusive-card:nth-child(even) {
    animation-delay: 0.2s;
}

.bonus-card:nth-child(even) {
    animation-delay: 0.2s;
}

.testimonial-card:nth-child(even) {
    animation-delay: 0.2s;
}

/* Animação de entrada para elementos visíveis */
.animate-on-scroll {
    opacity: 0;
    transform: translateY(30px);
    transition: all 0.6s ease;
}

.animate-on-scroll.visible {
    opacity: 1;
    transform: translateY(0);
}

/* Sistema de Notificações */
.notification-container {
    position: fixed;
    top: 80px;
    right: 20px;
    z-index: 9999;
    display: flex;
    flex-direction: column;
    gap: 15px;
    max-width: 180px;
    pointer-events: none;
}

.notification {
    background: white;
    padding: 8px 10px;
    border-radius: 12px;
    box-shadow: 0 4px 20px rgba(0,0,0,0.15);
    display: flex;
    align-items: flex-start;
    pointer-events: all;
    animation: slideInRight 0.4s ease, fadeOut 0.4s ease 4.6s;
    border: 2px solid var(--primary-green);
    position: relative;
}



@keyframes slideInRight {
    from {
        transform: translateX(400px);
        opacity: 0;
    }
    to {
        transform: translateX(0);
        opacity: 1;
    }
}

@keyframes fadeOut {
    to {
        opacity: 0;
        transform: translateX(400px);
    }
}


.notification-content {
    flex: 1;
    display: flex;
    flex-direction: column;
    gap: 2px;
}

.notification-name {
    font-weight: 700;
    font-size: 0.95rem;
    color: #1a1a1a;
    line-height: 1.2;
}

.notification-product {
    font-size: 0.82rem;
    color: #666;
    line-height: 1.2;
}

.notification-location {
    font-size: 0.75rem;
    color: #999;
    line-height: 1.2;
}

.notification-icon {
    width: 32px;
    height: 32px;
    background: linear-gradient(135deg, #10B981 0%, #34D399 100%);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1rem;
    flex-shrink: 0;
    margin-right: 10px;
    box-shadow: 0 3px 10px rgba(16, 185, 129, 0.4);
}

@media (max-width: 768px) {
    .notification-container {
        top: 10px;
        right: 10px;
        left: auto;
        max-width: 280px;
    }

    .notification {
        padding: 8px 12px;
        border-radius: 10px;
        border-width: 1px;
    }

    .notification-icon {
        width: 24px;
        height: 24px;
        font-size: 0.8rem;
        margin-right: 8px;
        box-shadow: 0 1px 4px rgba(34, 197, 94, 0.25);
    }

    .notification-name {
        font-size: 0.85rem;
        font-weight: 600;
    }

    .notification-product {
        font-size: 0.75rem;
    }

    .notification-location {
        font-size: 0.7rem;
    }

    .notification-content {
        gap: 1px;
    }
}

