.popup-card .popup-buttons {
    display: flex;
    flex-direction: column;
    gap: 10px;
}

.popup-btn {
    padding: 14px 24px;
    border: none;
    border-radius: 8px;
    font-size: 0.95rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    text-decoration: none;
    text-align: center;
    display: block;
}

.popup-btn-accept {
    background: var(--primary-green);
    color: white;
    box-shadow: 0 4px 12px rgba(40, 167, 69, 0.3);
}

.popup-btn-accept:hover {
    background: var(--dark-green);
    transform: translateY(-2px);
    box-shadow: 0 6px 16px rgba(40, 167, 69, 0.4);
}

.popup-btn-decline {
    background: transparent;
    color: #666;
    font-size: 0.9rem;
    font-weight: 500;
    border: 1px solid #ddd;
}

.popup-btn-decline:hover {
    color: #333;
    background: #f8f9fa;
}

@keyframes fadeIn {
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
}

@keyframes slideUp {
    from {
        transform: translateY(30px);
        opacity: 0;
    }
    to {
        transform: translateY(0);
        opacity: 1;
    }
}

@media (max-width: 480px) {
    .popup-content {
        padding: 25px 20px;
        max-width: 340px;
    }

    .popup-title {
        font-size: 1.3rem;
    }

    .popup-new-price {
        font-size: 1.8rem;
    }
}

